import React from 'react';
import { View, Text, ImageBackground, StyleSheet} from 'react-native';
import {useFonts} from 'expo-font';

const ContemplacaoMisterios = () =>{
    const [loaded] = useFonts({
        MontserratExtraBold: require('../../../assets/fonts/Montserrat-ExtraBold.ttf'),
        MontserratExtraBoldItalic: require('../../../assets/fonts/Montserrat-ExtraBoldItalic.ttf'),
        MontserratBold: require('../../../assets/fonts/Montserrat-Bold.ttf'),
      });
      if (!loaded) {
        return null;
      }
    
    return(
    <View style={{flex: 1, backgroundColor: 'blue'}}>
        <ImageBackground source={require('../../../assets/misterios/1misteriogozoso.jpg')} imageStyle={{ height: '100%'}} style={{flex: 1,  flexDirection: 'column',justifyContent: 'flex-end'}}>
        <Text style={styles.textMisterio}>1º MISTÉRIO</Text>
        <Text style={styles.textContemplacao}>Anunciação do Arcanjo Gabriel</Text>
        <Text style={styles.textOfereciemento}>Nós vos oferecemos, Senhor Jesus, esta primeira dezena, em honra a vossa Encarnação no seio de Maria; e vos pedimos, por esse mistério, e por sua intercessão uma profunda humildade. Assim seja</Text>
        <Text style={styles.textOracao}>Pai-Nosso, dez Ave-Marias, Glória-ao-Pai;</Text>
        </ImageBackground>
    </View>
    );
}


const styles = StyleSheet.create({
    textMisterio:{
     fontFamily: 'MontserratExtraBold',
     fontSize: 22,
     color:'white', 
     textAlign: 'center',
     marginBottom: 10
    },
    textContemplacao:{
        fontFamily: 'MontserratExtraBoldItalic',
        fontSize: 19,
        color:'white', 
        textAlign: 'center',
        marginBottom: 10
    },
    textOfereciemento:{
        fontFamily: 'MontserratBold',
        fontSize: 13,
        color:'white', 
        textAlign: 'center',
        marginBottom: 10

    },
    textOracao:{
        fontFamily: 'MontserratExtraBoldItalic',
        fontSize: 15,
        color:'white', 
        textAlign: 'center',
        marginBottom: 20

    }
   });
export default ContemplacaoMisterios;
