import React from 'react';
import { View, Text, ImageBackground} from 'react-native';
import {useFonts} from 'expo-font';
import img1 from '../../../../../assets/misterios/gozosos.jpg';


const Item = ({titulo, desc,img, id}) =>{
    const [loaded] = useFonts({
        Bold: require('../../../../../assets/fonts/Montserrat-SemiBold.ttf'),
    });
  
      if (!loaded) {
        return null;
      }


    return(
    <View style={{alignItems: 'center', marginBottom: 90}}>
      <View style={{width: '85%'}}>
        <ImageBackground source={{uri: img1}} imageStyle={{ borderRadius: 5, height: 95}} style={{marginBottom: 0}}>
          <Text style={{fontFamily: 'Bold', fontSize: 22, color:'white', textAlign: 'center', position: 'relative', top: 35 }}>{titulo}</Text>
        </ImageBackground>
      </View>
    </View>
    );
}

export default Item;
